export class Camara{constructor(t){this.videoNode=t}encender(){navigator.mediaDevices.getUserMedia({audio:!1,video:{width:300,height:300},facingMode:"environment"}).then(t=>{this.videoNode.srcObject=t,this.stream=t}).catch(t=>{console.log(t)})}apagar(){this.videoNode.pause(),this.stream&&this.stream.getTracks()[0].stop()}tomarFoto(){let t=document.createElement("canvas");t.setAttribute("width",300),t.setAttribute("height",300);let e=t.getContext("2d");return e.drawImage(this.videoNode,0,0,t.width,t.height),this.foto=e.canvas.toDataURL(),t=null,e=null,this.foto}}